(103, 152, 'Frontpage top teaser', '', '<div class="uk-height-1-1 uk-vertical-align uk-text-center-small uk-text-center-medium">\n  <div class="uk-width-1-1 uk-width-medium-1-2 uk-vertical-align-middle">\n          <h1 class="tm-teaser-text-primary" data-uk-scrollspy="{cls:''uk-animation-fade'', repeat: true}">\n            APRIL THEME\n          </h1>\n          <p class="tm-teaser-text-secondary" data-uk-scrollspy="{cls:''uk-animation-fade'', repeat: true, delay: 300}">\n            Available for Joomla and WordPress<br>\n            Build with UIkit and Warp7.\n            </p>\n          <button class="uk-button uk-button-large tm-button-teaser" data-uk-scrollspy="{cls:''uk-animation-fade'', repeat: true, delay: 600}">\n            download digit\n          </button>\n      <p data-uk-scrollspy="{cls:''uk-animation-fade'', repeat: true, delay: 900}">\n        <i class="uk-icon-comment"></i> <b>194</b> comments <i class="uk-icon-heart tm-margin-left"></i> <b>5.256</b> likes\n      </p>\n       <img data-uk-scrollspy="{cls:''uk-animation-slide-right'', repeat: true}" src="images/yootheme/demo/default/home_phone-big.png" height="550" width="500" alt="Demo" class="uk-visible-large" style="position: absolute; bottom: 0; right:0;">\n    <img data-uk-scrollspy="{cls:''uk-animation-slide-left'', repeat: true, delay: 300}" src="images/yootheme/demo/default/home_phone-small.png" height="450" width="500" alt="Demo" class="uk-visible-large" style="position: absolute; bottom: 0; right:60px;">\n    </div>\n</div>', 1, 'top-teaser', 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'mod_custom', 1, 0, '{"prepare_content":"0","backgroundimage":"","layout":"_:default","moduleclass_sfx":"","cache":"1","cache_time":"900","cachemode":"static","module_tag":"div","bootstrap_size":"0","header_tag":"h3","header_class":"","style":"0"}', 0, '*'),
(104, 153, 'Home Team', '', '<img class="uk-align-center" src="images/yootheme/demo/default/home_worldmap.png" height="390" width="980" alt="Demo">\n<h1 class="tm-negativ-margin-top uk-text-center">\n  Connected all over the world\n</h1>\n<p class="uk-width-medium-2-3 uk-align-center uk-text-center">\n  <mark class="tm-mark-scribble">To build awesome webapplication, we need global minds and innovation to convince the web.</mark>\n</p>\n\n<hr class="uk-grid-divider">\n\n<div class="uk-grid">\n  <div class="uk-width-medium-1-2">\n    \n    <div class="uk-grid">\n\n      <div class="uk-width-large-2-10 uk-width-medium-3-10 uk-width-1-1 uk-text-center">\n        <img src="images/yootheme/home_team-1.jpg" class="uk-margin-bottom tm-image-round uk-thumbnail" alt="CEO Image">\n      </div>\n      <div class="uk-width-large-8-10 uk-width-medium-7-10 uk-width-1-1 uk-text-center-small">\n        <p class="uk-text-bold">\n          As the CEO of the digit team, I''m responsible for your success.\n      </p>\n      <p>\n        <i>„Coming together is a beginning; keeping together is progress; working together is success“</i>\n      </p>\n        <p class="tm-primary-color uk-text-large">\n          CEO, Dave White\n        </p>\n      </div>\n\n  </div>\n  </div>\n  <div class="uk-width-medium-1-2">\n    \n    <div class="uk-grid">\n\n      <div class="uk-width-large-2-10 uk-width-medium-3-10 uk-width-1-1 uk-float-left uk-text-center">\n        <img src="images/yootheme/home_team-2.jpg" class="uk-margin-bottom tm-image-round uk-thumbnail" alt="CCO Image">\n      </div>\n      <div class="uk-width-large-8-10 uk-width-medium-7-10 uk-width-1-1 uk-text-center-small">\n        <p class="uk-text-bold">\n          I try to convert your business into an artwork of digit''al experience.\n      </p>\n      <p>\n        <i>„Almost always, the creative dedicated minority has made the world better.“</i>\n      </p>\n        <p class="tm-primary-color uk-text-large">\n          CCO, Julia Bl